import React, { useState, useEffect, useRef } from "react";
import "./ChatWidget.css";

export default function ChatWidget() {
  const [messages, setMessages] = useState([
    {
      from: "bot",
      text: "Hello üëã I‚Äôm your Salon Assistant. How can I help you today?",
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
      quickReplies: ["Book haircut", "View appointments", "Cancel appointment"]
    }
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleQuickReply = (replyText) => {
    setInput(replyText);
    sendMessage(replyText);
  };

  const sendMessage = async (overrideText) => {
    const finalText = overrideText || input;
    if (!finalText.trim()) return;

    const userMessage = {
      from: "user",
      text: finalText,
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
    };

    setMessages(prev => [...prev, userMessage]);
    setInput("");
    setLoading(true);

    try {
      const res = await fetch("http://localhost:4000/api/agent/message", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: finalText })
      });

      const data = await res.json();

      const botMessage = {
        from: "bot",
        text: data.reply || "‚ö†Ô∏è Something went wrong",
        time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
        quickReplies: data.suggestions || []
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (err) {
      setMessages(prev => [
        ...prev,
        {
          from: "bot",
          text: "‚ö†Ô∏è Error connecting to server",
          time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
        }
      ]);
    }
    setLoading(false);
  };

  return (
    <div className="chat-widget">
      <div className="chat-widget-header">üíá Salon Assistant</div>

      <div className="chat-widget-messages">
        {messages.map((m, idx) => (
          <div key={idx} className={`message ${m.from}`}>
            {m.from === "bot" && <div className="avatar">ü§ñ</div>}
            <div>
              <div className="message-text">{m.text}</div>
              <div className="message-time">{m.time}</div>

              {m.quickReplies && m.quickReplies.length > 0 && (
                <div className="quick-replies">
                  {m.quickReplies.map((qr, i) => (
                    <button key={i} onClick={() => handleQuickReply(qr)}>{qr}</button>
                  ))}
                </div>
              )}
            </div>
            {m.from === "user" && <div className="avatar">üßë</div>}
          </div>
        ))}
        {loading && (
          <div className="typing-indicator">
            ü§ñ Salon Assistant is typing <span className="animate-pulse">...</span>
          </div>
        )}
        <div ref={messagesEndRef}></div>
      </div>

      <div className="chat-widget-input">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
          placeholder="Type a message..."
        />
        <button onClick={() => sendMessage()}>Send</button>
      </div>
    </div>
  );
}
