import React, { useState, useEffect, useRef } from "react";

export default function ChatWidget() {
  const [messages, setMessages] = useState([
    {
      from: "bot",
      text: "Hello üëã I‚Äôm your Salon Assistant. How can I help you today?",
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
      quickReplies: ["Book haircut", "View appointments", "Cancel appointment"]
    }
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleQuickReply = (replyText) => {
    setInput(replyText);
    sendMessage(replyText);
  };

  const sendMessage = async (overrideText) => {
    const finalText = overrideText || input;
    if (!finalText.trim()) return;

    const userMessage = {
      from: "user",
      text: finalText,
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
    };

    setMessages(prev => [...prev, userMessage]);
    setInput("");
    setLoading(true);

    try {
      const res = await fetch("http://localhost:4000/api/agent/message", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: finalText })
      });

      const data = await res.json();

      const botMessage = {
        from: "bot",
        text: data.reply || "‚ö†Ô∏è Sorry, something went wrong.",
        time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
        quickReplies: data.suggestions || [] // backend can send suggestions
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (err) {
      setMessages(prev => [
        ...prev,
        {
          from: "bot",
          text: "‚ö†Ô∏è Error connecting to server.",
          time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
        }
      ]);
    }

    setLoading(false);
  };

  return (
    <div className="flex flex-col w-96 h-[550px] border rounded-xl shadow-lg bg-white overflow-hidden">
      {/* Header */}
      <div className="bg-green-600 text-white p-3 font-semibold text-lg">
        üíá Salon Assistant
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-3 bg-gray-50">
        {messages.map((m, idx) => (
          <div key={idx} className={`flex mb-4 ${m.from === "user" ? "justify-end" : "justify-start"}`}>
            {m.from === "bot" && (
              <div className="w-8 h-8 rounded-full bg-green-600 text-white flex items-center justify-center mr-2">
                ü§ñ
              </div>
            )}
            <div>
              <div
                className={`px-3 py-2 rounded-lg shadow ${
                  m.from === "user"
                    ? "bg-blue-500 text-white rounded-br-none"
                    : "bg-white border rounded-bl-none"
                }`}
              >
                {m.text}
              </div>
              <div className="text-xs text-gray-500 mt-1">{m.time}</div>

              {/* Quick replies */}
              {m.quickReplies && m.quickReplies.length > 0 && (
                <div className="flex flex-wrap gap-2 mt-2">
                  {m.quickReplies.map((qr, i) => (
                    <button
                      key={i}
                      onClick={() => handleQuickReply(qr)}
                      className="bg-gray-200 hover:bg-green-500 hover:text-white text-sm px-3 py-1 rounded transition"
                    >
                      {qr}
                    </button>
                  ))}
                </div>
              )}
            </div>
            {m.from === "user" && (
              <div className="w-8 h-8 rounded-full bg-blue-500 text-white flex items-center justify-center ml-2">
                üßë
              </div>
            )}
          </div>
        ))}

        {loading && (
          <div className="flex items-center space-x-2 text-gray-500 text-sm">
            <span>ü§ñ Salon Assistant is typing</span>
            <span className="animate-pulse">...</span>
          </div>
        )}

        <div ref={messagesEndRef}></div>
      </div>

      {/* Input */}
      <div className="flex p-2 border-t bg-white">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
          placeholder="Type a message..."
          className="flex-1 border rounded px-2 py-2 focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <button
          onClick={() => sendMessage()}
          className="ml-2 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
        >
          Send
        </button>
      </div>
    </div>
  );
}
